import React, { useState } from 'react'
import './page.css'

export default function HeartClicker() {
  const [hearts, setHearts] = useState([])

  const handleClick = e => {
    const rect = e.currentTarget.getBoundingClientRect()
    const x = e.clientX - rect.left
    const y = e.clientY - rect.top
    const id = Date.now()
    const rotation = Math.random() * 30 - 15         // ±15°
    const duration = 1000 + Math.random() * 500      // 1–1.5s
    setHearts(h => [...h, { x, y, id, rotation, duration }])

    setTimeout(() => {
      setHearts(h => h.filter(item => item.id !== id))
    }, duration)
  }

  return (
    <div className="click-area" onClick={handleClick}>
      {hearts.map(h => (
        <div
          key={h.id}
          className="heart"
          style={{
            left: h.x,
            top: h.y,
            '--rotation': `${h.rotation}deg`,
            '--duration': `${h.duration}ms`
          }}
        >
          ❤️
        </div>
      ))}
      <p>Click anywhere to make hearts float and pop!</p>
    </div>
  )
}
